import React, { useState } from 'react'
import { posts } from '../utils/dummyData' 

const Home = () => {
  const numOfPages = 1; 
  const [pages, setPages] = useState(1);


  if (posts.length < 1) {
    return (
      <div className='w-full h-screen px-8 flex place-items-center justify-center'>
        <span className='text-3xl text-slate-500'>No Post Available</span>
      </div>
    );
  }

  return (
    <div className='py-10 2xl:py-5'>
      <div className="posts-container">
        {posts.map((post) => (
          <div key={post.id} className="post-card border p-4 mb-4 rounded-lg shadow-lg">
            <h2 className="text-xl font-semibold text-gray-800">{post.title}</h2>
            <p className="text-sm text-gray-500">Category : {post.cat}</p>
            <img src={post.img} alt="" className='w-5'/>
            <p className='text-sm text-gray-500'>Author: {post.user.name}</p>
            
          </div>
        ))}
      </div>
      <div className="pagination mt-4 text-center">
        <button
          onClick={() => setPages(pages > 1 ? pages - 1 : pages)}
          disabled={pages === 1}
          className="px-4 py-2 border rounded-md text-white bg-gray-700 hover:bg-gray-600"
        >
          Prev
        </button>
        <span className="mx-4 text-lg font-medium text-gray-600">{`Page ${pages} of ${numOfPages}`}</span>
        <button
          onClick={() => setPages(pages < numOfPages ? pages + 1 : pages)}
          disabled={pages === numOfPages}
          className="px-4 py-2 border rounded-md text-white bg-gray-700 hover:bg-gray-600"
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default Home;
