import { useGoogleLogin } from "@react-oauth/google";
import React, { useState } from "react";
import { FcGoogle } from "react-icons/fc";
import { Toaster, toast } from "sonner";
import { Link } from "react-router-dom";
import Logo from "../components/Logo";
import { Button } from "../components";
import Divider from "../components";
import InputBox from "../components/Inputbox"; // Assuming Inputbox is a custom component

const LoginPage = () => {
  const user = { token: null };

  if (user.token) {
    window.location.replace("/");
  }

  const [data, setData] = useState({
    email: "",
    password: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setData({
      ...data,
      [name]: value,
    });
  };

  // Google login logic
  const googleLogin = useGoogleLogin({
    onSuccess: (response) => {
      // Handle successful Google login here
      console.log(response);
    },
    onError: (error) => {
      // Handle error during Google login
      console.error(error);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the email/password login logic here
    console.log(data);
  };

  return (
    <div className="w-full h-screen flex flex-col md:flex-row">
      {/* Sidebar for larger screens */}
      <div className="hidden md:flex flex-col gap-y-4 w-1/3 min-h-screen bg-black items-center justify-center p-4">
        <Logo type="login" />
        <span className="text-xl font-semibold text-white">Welcome back!</span>
      </div>

      {/* Main content area */}
      <div className="flex flex-1 h-full items-center justify-center bg-white dark:bg-gradient-to-b md:dark:bg-gradient-to-r from-black via-[#434343] to-black px-6 md:px-10 lg:px-20">
        <div className="w-full flex flex-col items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
          <div className="block mb-10 md:hidden">
            <Logo />
          </div>

          <div className="max-w-md w-full space-y-8">
            <div>
              <h2 className="mt-6 text-center text-2xl md:text-3xl font-semibold text-gray-900 dark:text-white">
                Sign in to your account
              </h2>
            </div>

            {/* Google Sign In Button */}
            <Button
              label="Sign in with Google"
              icon={<FcGoogle />}
              styles="w-full flex items-center justify-center gap-4 bg-white dark:bg-transparent text-black dark:text-white px-5 py-2.5 rounded-full border border-gray-300 shadow-sm hover:bg-gray-100 dark:hover:bg-gray-800"
              onClick={googleLogin}
            />

            <Divider label="or sign in with email" />

            {/* Email and Password form */}
            <form onSubmit={handleSubmit} className="mt-8 space-y-6">
              <div className="flex flex-col rounded-md shadow-sm -space-y-px gap-5">
                <InputBox
                  type="email"
                  label="Email"
                  name="email"
                  value={data.email}
                  onChange={handleInputChange}
                  placeholder="your@gmail.com"
                />
                <InputBox
                  type="password"
                  label="Password"
                  name="password"
                  value={data.password}
                  onChange={handleInputChange}
                  placeholder="********"
                />
              </div>

              <div className="flex items-center justify-between mt-4">
                <div className="text-sm">
                  <Link to="/forgot-password" className="font-medium text-blue-600 hover:text-blue-500">
                    Forgot your password?
                  </Link>
                </div>
              </div>

              <div>
                <Button
                  label="Sign In"
                  styles="w-full bg-blue-600 hover:bg-blue-700 text-white px-5 py-2.5 rounded-full shadow-sm"
                  type="submit"
                />
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Toast notification */}
      <Toaster position="top-center" />
    </div>
  );
};

export default LoginPage;
