import React, { useState } from "react";
import { AiOutlineClose } from "react-icons/ai";
import { FaFacebook, FaInstagram, FaTwitterSquare } from "react-icons/fa";

import { Link } from "react-router-dom";
import UserProfile from "../assets/profile.png";
import Logo from "./Logo";
import ThemeSwitch from "./Switch";
import useStore from "../store";


function getInitials(fullName) {
  const names = fullName.split(" ");

  const initials = names.slice(0, 2).map((name) => name[0].toUpperCase());

  const initialsStr = initials.join(" ");

  return initialsStr;
}

const Navbar = () => {
  const {user, signOut} = useStore();

  const [showProfile, setShowProfile] = useState(false);
  return (
    <div className="flex flex-col md:flex-row w-full py-5 items-center justify-between gap-4 md:gap-0">
      <div className="flex gap-2 text-[20px] md:hidden lg:flex">
        <Link to="/" className="text-blue-600">
          <FaFacebook />
        </Link>
        <Link to="/" className="text-rose-600">
          <FaInstagram />
        </Link>
        <Link to="/" className="text-blue-500">
          <FaTwitterSquare />
        </Link>
      </div>

      <Logo />

      <div className="hidden md:flex gap-14 items-center">
        <ul className="flex gap-8 text-base text-black dark:text-white">
          <Link to="/">Home</Link>
          <Link to="/">Contact</Link>
          <Link to="/">About</Link>
        </ul>
        <ThemeSwitch />

        <div className='flex gap-1 items-center cursor-pointer'>
                {user?.user.image ? (
                  <img
                    src={user?.user.image}
                    alt='Profile'
                    className='w-8 h-8 rounded-full'
                  />
                ) : (
                  <span className='text-white w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center'>
                    {getInitials(user?.user.name)}
                  </span>
                )}
                <span className='font-medium text-black dark:text-gray-500'>
                  {user?.user?.name?.split(" ")[0]}
                </span>
              </div>

      </div>
    </div>
  );
};

export default Navbar;
