import { create } from "zustand";

const useStore = create((set) => ({
  user: JSON.parse(localStorage.getItem("user")) || {},
  isLoading: false,
  signIn: (data) => set({ user: data }),
  signOut: () => set({ user: {} }),
  setIsLoading: (val) => set({ isLoading: val }),
}));

export default useStore;
