import { Routes, Route, Outlet } from "react-router-dom";

function Layout() {
  return (
    <div className="w-full flex flex-col min-h-screen px-4 md:px-10 2xl:px-29=8">
      {/* navbar */}
      <div>
        <Outlet className="flex-1" />
      </div>
      {/* footer */}
    </div>
  );
}

function App() {
  const theme = "dark";
  return (
    <main classname={theme}>
      <div classname="w-full min-h-screen relative bg-white dark:bg-[#020b19]">
        <Routes>
          <Route element={<Layout />} />  
          <Route element={<Home/>}/>
          <Route element={<CategoriesPage/>}/>
          <Route element={<BlogDetails/>}/>
          <Route element={<WritePage/>}/>
        </Routes>
      </div>
    </main>
  );
}

export default App;
