import { useGoogleLogin } from "@react-oauth/google";
import React, { useState } from "react";
import { FcGoogle } from "react-icons/fc";
import { Toaster, toast } from "sonner";
import { Link } from "react-router-dom";
import Logo from "../components/Logo";

const LoginPage = () => {
  const user = { token: null };

  if (user.token) {
    window.location.replace("/");
  }

  return (
    <div className="w-full h-[100vh]">
      {" "}
      {/* Fixed syntax issue */}
      <div className="hidden md:flex flex-col gap-y-4 w-1/4 min-h-screen bg-black items-center justify-between">
        <Logo type="login" className="" />
        <span className="text-xl font-semibold text-white">Welcome, back!</span>
      </div>
      <div className="flex w-full md:w-2/3 h-full bg-white dark:bg-gradient-to-b md:dark:bg-gradient-to-r from-black via-[#71b3e] to-black items-center px-10 md:px-20 lg:px-40">
      <div className="w-full flex flex-col items-center justify-center py-12 px-4 sm:px-6 lg:px-8"></div>
      </div>
    </div>
  );
};

export default LoginPage;
